import { IActions } from '../../interfaces';
import { LOADING, LOADED } from '../types';
const initialState = false;
export default (state = initialState, action: IActions) => {
	switch (action.type) {
		case LOADING:
			return state;
		case LOADED:
			return (state = true);
		default:
			return state;
	}
};
