import React, { useState } from 'react';
import { SearchOutlined } from '@ant-design/icons';
import { Input } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import searchFor from '../redux/actions/searchFor';

const Searchbar: React.FC = () => {
	const searchResult = useSelector((state) => state);
	const dispatch = useDispatch();

	const [query, setQuery] = useState('');

	const handlerSearch = (event: React.FormEvent<HTMLFormElement>) => {
		event.preventDefault();
		dispatch(searchFor(query));
	};

	return (
		<div className='gi_width_res'>
			<form onSubmit={handlerSearch} name='searchForm'>
				<Input
					size='large'
					prefix={<SearchOutlined className='searchIcon' />}
					placeholder='Search...'
					className='searchBar'
					type='Search'
					value={query}
					onChange={(e) => setQuery(e.target.value)}
				/>
				<input type='submit' value='search' />
			</form>
		</div>
	);
};

export default Searchbar;
