import { IActions } from '../../interfaces';
import { LIGHT_THEME, DARK_THEME } from '../types';

const initstate: boolean = false;
export default (state = initstate, action: IActions) => {
	switch (action.type) {
		case LIGHT_THEME:
			return (state = true);
		case DARK_THEME:
			return (state = false);
		default:
			return state;
	}
};
