import React, { useState, useRef } from 'react';
import { Input } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import { RouteProps } from 'react-router-dom';

const { Search } = Input;

interface searchBarProps {
	history: RouteProps;
}

const Searchbar: React.FC<searchBarProps> = ({ history }) => {
	const [query, setQuery] = useState('');
	const searchForm = useRef(HTMLFormElement);

	console.log(history);
	const handlerSearch = (event: React.FormEvent<HTMLFormElement>) => {
		event.preventDefault();

		console.log(event);
		// console.log(event);
	};

	const searchInputEvent = (event: React.KeyboardEvent<HTMLInputElement>) => {
		// console.log(event.target.value);

		if (event.keyCode === 13) {
			searchForm.current.submit();
		}
	};

	return (
		<div className='gi_width_res'>
			<form onSubmit={handlerSearch} ref={searchForm}>
				<Input
					size='large'
					prefix={<SearchOutlined className='searchIcon' />}
					placeholder='Search...'
					className='searchBar'
					type='Search'
					onKeyUp={searchInputEvent}
				/>
			</form>
		</div>
	);
};

export default Searchbar;
